mapper.select:
select s.id, scheduled_id, s.doctor_id, s.time, s.start_time, s.end_time, s.number, s.remainder,  s.status, s.create_by, s.create_time, s.update_by,
    s.update_time, s.remark, u.nick_name as doctor_name  from t_doctor_scheduled s LEFT JOIN sys_user u ON s.doctor_id = user_id

    s.del_flag = 0
     and s.scheduled_id = #{scheduledId}
     and s.doctor_id = #{doctorId}
     and s.time = #{time}
     and s.start_time = #{startTime}
     and s.end_time = #{endTime}
     and s.number = #{number}
     and s.remainder = #{remainder}
     and s.status = #{status}
     and u.nick_name like concat('%', #{doctorName}, '%')

select s.id, scheduled_id, s.doctor_id, s.time, s.start_time, s.end_time, s.number, s.remainder,  s.status, s.create_by, s.create_time, s.update_by,
s.update_time, s.remark, u.nick_name as doctor_name  from t_doctor_scheduled s LEFT JOIN sys_user u ON s.doctor_id = user_id

    s.del_flag = 0 and s.start_time > now()
     and s.scheduled_id = #{scheduledId}
     and s.doctor_id = #{doctorId}
     and s.time = #{time}
     and s.start_time = #{startTime}
     and s.end_time = #{endTime}
     and s.number = #{number}
     and s.remainder = #{remainder}
     and s.status = #{status}
     and u.nick_name like concat('%', #{doctorName}, '%')

mapper.update:
update t_doctor_scheduled
scheduled_id = #{scheduledId}, doctor_id = #{doctorId}, time = #{time}, start_time = #{startTime}, end_time = #{endTime}, number = #{number}, remainder = #{remainder}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
