mapper.select:
select s.scheduled_id,s. doctor_id, s.time, s.status, s.create_by, s.create_time, s.update_by, s.update_time, u.remark, u.nick_name as doctor_name ,u.avatar from t_scheduled s LEFT JOIN sys_user u ON s.doctor_id = user_id

    s.del_flag = 0
     and s.time = #{time}
     and s.status = #{status}
     and u.nick_name like concat('%', #{doctorName}, '%')
     and u.dept_id = #{deptId}

select s.scheduled_id,s. doctor_id, s.time, s.status, s.create_by, s.create_time, s.update_by, s.update_time, u.remark, u.nick_name as doctor_name ,u.avatar from t_scheduled s LEFT JOIN sys_user u ON s.doctor_id = user_id

    s.del_flag = 0  AND s.del_flag  = 0 AND s.time >=  #{time} and s.time = #{time}
     and s.status = #{status}
     and u.nick_name like concat('%', #{doctorName}, '%')
     and u.dept_id = #{deptId}

mapper.update:
update t_scheduled
doctor_id = #{doctorId}, time = #{time}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where scheduled_id = #{scheduledId}
