mapper.select:
select q.questionnaire_id, q.questionnaire_name, q.create_by, q.create_time, q.update_by, q.update_time, q.remark, q.publish_date, q.question_count, q.participant_count, q.status
from survey_questionnaire q
left join survey_user_questionnaire uq on q.questionnaire_id = uq.questionnaire_id
where uq.user_id = #{userId}

and questionnaire_name like concat('%', #{questionnaireName}, '%')

and status = #{status}

mapper.update:
update survey_questionnaire
questionnaire_name = #{questionnaireName}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, publish_date = #{publishDate}, question_count = #{questionCount}, participant_count = #{participantCount}, status = #{status},
where questionnaire_id = #{questionnaireId}

update survey_questionnaire
status = 1, publish_date = Now()
where questionnaire_id = #{questionnaireId}

update survey_questionnaire
set question_count = question_count+1
where questionnaire_id = #{quesitonnaireId}

update survey_questionnaire
set question_count = question_count -1
where questionnaire_id = #{questionnaireId}

update survey_questionnaire
set participant_count = participant_count +1
where questionnaire_id = #{questionnaireId}

update survey_questionnaire
set participant_count = participant_count -1
where questionnaire_id = #{questionnaireId}

update survey_questionnaire
set status = #{status}
where questionnaire_id = #{questionnaireId}
